CREATE SCHEMA PMS AUTHORIZATION DBACREATE TABLE pms.allowedclan (  AllowedClanId int GENERATED BY DEFAULT AS IDENTITY (START WITH 1  INCREMENT BY 1),  ClanId BIGINT NOT NULL,  ClanName varchar(45) NOT NULL,  PRIMARY KEY (AllowedClanId));CREATE TABLE pms.attendance(  AttendanceId int GENERATED BY DEFAULT AS IDENTITY (START WITH 1  INCREMENT BY 1),  AccountId BIGINT NOT NULL,  ClanId BIGINT NOT NULL,  CheckInTime timestamp DEFAULT CURRENT_TIMESTAMP,  PRIMARY KEY (AttendanceId));CREATE TABLE pms.attendancePayout (  AttendanceId int NOT NULL,  PayoutId int NOT NULL);CREATE TABLE pms.callerattendance (  CallerAttendanceId int GENERATED BY DEFAULT AS IDENTITY (START WITH 1  INCREMENT BY 1),  AccountId BIGINT NOT NULL,  ClanId BIGINT NOT NULL,  BattleTime timestamp DEFAULT CURRENT_TIMESTAMP,  MapName varchar(45)  DEFAULT ' ',  PRIMARY KEY (CallerAttendanceId));CREATE TABLE pms.callerpayout (  CallerAttendanceId int NOT NULL,  PayoutId int NOT NULL);CREATE TABLE pms.campaignreward (  CampaignRewardId int GENERATED BY DEFAULT AS IDENTITY (START WITH 1  INCREMENT BY 1),  ClanId BIGINT NOT NULL,  StartTime timestamp NOT NULL,  EndTime timestamp NOT NULL,  CampaignName varchar(100) NOT NULL,  CampaignRewardTime timestamp DEFAULT CURRENT_TIMESTAMP,  Amount int NOT NULL,  PRIMARY KEY (CampaignRewardId));CREATE TABLE pms.campaignrewardpayout (  CampaignRewardId int NOT NULL,  PayoutId int NOT NULL,  AccountId int NOT NULL);CREATE TABLE pms.event (  EventId int GENERATED BY DEFAULT AS IDENTITY (START WITH 1  INCREMENT BY 1),  ClanId BIGINT NOT NULL,  Type varchar(45) NOT NULL,  StartTime timestamp NOT NULL,  ImageUrl varchar(200) NOT NULL,  PRIMARY KEY (EventId));CREATE TABLE pms.incentive(  IncentiveId int GENERATED BY DEFAULT AS IDENTITY (START WITH 1  INCREMENT BY 1),  AccountId BIGINT NOT NULL,  ClanId BIGINT NOT NULL,  TankUnlockedName varchar(45) NOT NULL,  TankUnlockedId int NOT NULL,  IncentiveTime timestamp DEFAULT CURRENT_TIMESTAMP,  PRIMARY KEY (IncentiveId));CREATE TABLE pms.incentivePayout (  IncentiveId int NOT NULL,  PayoutId int NOT NULL);CREATE TABLE pms.localclansetting (  LocalClanSettingId int GENERATED BY DEFAULT AS IDENTITY (START WITH 1  INCREMENT BY 1),  ClanId BIGINT NOT NULL,  BattleTimeRangeBeforeMinutes int DEFAULT 30,  BattleTimeRangeAfterMinutes int DEFAULT 30,  BattleDayGmtOffset float DEFAULT 2,  CallerAmountPerBattle int DEFAULT 200,  PlayerIsBannedError varchar(200) DEFAULT 'Serb has denied your pay. Better luck next time, tovarishch',  PlayerAlreadyCheckedInError varchar(200) DEFAULT 'Stop it. You''ve checked in. Quit flooding the tubes.',  PlayerNotWithinTimeFrameError varchar(200) DEFAULT 'You''re too early. Or too late. Either way fuck off and try again at the right time.',  DefaultError varchar(200) DEFAULT 'Hmm, seems like something went wrong with the request. See if you can try again.',  PRIMARY KEY (LocalClanSettingId),  UNIQUE (ClanId));CREATE TABLE pms.mapname (	MapNameId int GENERATED BY DEFAULT AS IDENTITY (START WITH 1  INCREMENT BY 1),	Name varchar(45) NOT NULL,	PRIMARY KEY (MapNameId));CREATE TABLE pms.newscarousel (  NewsCarouselId int GENERATED BY DEFAULT AS IDENTITY (START WITH 1  INCREMENT BY 1),  ClanId BIGINT NOT NULL,  CreatedByAccountId BIGINT NOT NULL,  ModifiedByAccountId BIGINT NOT NULL,  Title varchar(45) DEFAULT NULL,  Text varchar(200) DEFAULT NULL,  SmallScreenOnlyText TINYINT NOT NULL,  LargeBannerUrl varchar(200) NOT NULL,  MediumBannerUrl varchar(200) NOT NULL,  SmallBannerUrl varchar(200) NOT NULL,  IconImageUrl varchar(200) NOT NULL,  BannerBackgroundColor varchar(7) DEFAULT '#ffa500',  BannerTextColor varchar(7) DEFAULT '#ffffff',  Global TINYINT NOT NULL,  Active TINYINT NOT NULL,  PRIMARY KEY (NewsCarouselId));CREATE TABLE pms.payout (  PayoutId int  GENERATED BY DEFAULT AS IDENTITY (START WITH 1  INCREMENT BY 1),  TransactionId int NOT NULL,  Amount int NOT NULL,  Type varchar(45) NOT NULL,  PayerAccountId int NOT NULL,  PayoutTime timestamp DEFAULT CURRENT_TIMESTAMP,  PRIMARY KEY (PayoutId));CREATE TABLE pms.playerBonus(  PlayerBonusId int GENERATED BY DEFAULT AS IDENTITY (START WITH 1  INCREMENT BY 1),  AccountId BIGINT NOT NULL,  ClanId BIGINT NOT NULL,  Reason varchar(45) NOT NULL,  playerBonusTime timestamp DEFAULT CURRENT_TIMESTAMP,  PRIMARY KEY (PlayerBonusId));CREATE TABLE pms.playerBonusPayout (  PlayerBonusId int NOT NULL,  PayoutId int NOT NULL);CREATE TABLE pms.playersettings (  PlayerSettingsId int GENERATED BY DEFAULT AS IDENTITY (START WITH 1  INCREMENT BY 1),  AccountId BIGINT NOT NULL,  AdminReplayAutoCheckIn TINYINT DEFAULT '1',  PRIMARY KEY (PlayerSettingsId));CREATE TABLE pms.role (  RoleId int GENERATED BY DEFAULT AS IDENTITY (START WITH 1  INCREMENT BY 1),  RoleName VARCHAR(45) NOT NULL,  PRIMARY KEY (RoleId) );CREATE TABLE pms.transaction (  TransactionId int GENERATED BY DEFAULT AS IDENTITY (START WITH 1  INCREMENT BY 1),  ClanId BIGINT NOT NULL,  TransactionTime timestamp DEFAULT CURRENT_TIMESTAMP,  PRIMARY KEY (TransactionId));CREATE TABLE pms.userRole (  AccountId BIGINT NOT NULL,  RoleId int NOT NULL,  Nickname varchar(45) NOT NULL,  PRIMARY KEY (AccountId, RoleId));CREATE SEQUENCE payoutTransactionSequence AS INTEGER START WITH 1 INCREMENT BY 1;CREATE TABLE pms.TankIncentiveDefaultPayout (  TankIncentiveDefaultPayoutId int GENERATED BY DEFAULT AS IDENTITY (START WITH 1  INCREMENT BY 1),  ClanId BIGINT NOT NULL,  TankId BIGINT NOT NULL,  Amount int NOT NULL,  PRIMARY KEY (TankIncentiveDefaultPayoutId))